import React, { useEffect, useState } from 'react';
import './App.css';
import Preview from './components/Preview/index';
import Message from './components/Message';
import NotesContainer from './components/Notes/NotesContainer';
import NoteList from './components/Notes/NoteList';
import Note from './components/Notes/Note';
import NoteForm from './components/Notes/NoteForm';
import Alert from './components/Alert';

function App() {

  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectedNote, setSelectedNote] = useState(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState(false);
  const [validationErrors, setValidationErrors] = useState([])

  useEffect(() => {
    if (localStorage.getItem('notes')) {
      setNotes(JSON.parse(localStorage.getItem('notes')))
    }else {
      localStorage.setItem('notes', JSON.stringify([]))
    }
  }, []);

  useEffect(() => {
    if (validationErrors.length !== 0) {

    }
  })

  const saveToLocalStorage = (key, value) => {
    localStorage.setItem(key, JSON.stringify(value))
  }

  const validate = () => {
    const validationErrors = [];
    let passed = true;
    if (!title) {
      validationErrors.push('please enter title of notes');
      passed = false
    }
    if (!content) {
      validationErrors.push('please enter content of notes');
      passed = false
    }
    setValidationErrors(validationErrors);
    return passed
  }

  const changeTitleHandler = (event) => {
    setTitle(event.target.value)
  }

  const changeContentHandler = (event) => {
    setContent(event.target.value)
  }

  const saveNoteHandler = () => {

    if (!validate()) return;

    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const updatedNotes = [...notes, note];

    saveToLocalStorage('notes', updatedNotes)
    setNotes(updatedNotes);
    setCreating(false);
    setSelectedNote(note.id)
    setTitle('');
    setContent('');

  }

  const selectedNoteHandler = noteId => {
    setSelectedNote(noteId)
    setCreating(false)
    setEditing(false)
  }

  const editNoteHandler = () => {
    const note = notes.find(note => note.id === selectedNote);
    setEditing(true);
    setTitle(note.title);
    setContent(note.content)
  }

  const updatedNoteHandler = () => {
    if (!validate()) return;
    const updatedNotes = [...notes];
    const noteIndex = notes.findIndex(note => note.id === selectedNote)
    updatedNotes[noteIndex] ={
      id: selectedNote,
      title: title,
      content: content
    };

    saveToLocalStorage('notes', updatedNotes)
    setNotes(updatedNotes);
    setEditing(false);
    setTitle('');
    setContent('');

  }

  const deleteNoteHandler = () => {
    const updatedNotes = [...notes];
    const noteIndex = updatedNotes.findIndex(note => note.id === selectedNote);
    notes.splice(noteIndex, 1);
    saveToLocalStorage('notes', notes)
    setNotes(notes);
    setSelectedNote(null)
  }

  const getAddNote = () => {



    return (
      <NoteForm
        formTitle= "New Note"
        title = {title}
        content = {content}
        titleChanged = {changeTitleHandler}
        contentChanged = {changeContentHandler}
        submitText="save"
        submitClicked={saveNoteHandler}
      />
    );
  };

  const getPreview = () => {
    if(notes.length === 0) {
      return <Message title='Not Notes'/>
    }
    if(!selectedNote){
      return <Message title='Please Select Notes'/>
    }
    const note = notes.find(note => {
      return note.id === selectedNote;
    });

    let noteDisplay = (
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>
      </div>
    )

    if(editing){
    noteDisplay = (
      <NoteForm
      formTitle= "Edit Note"
      title = {title}
      content = {content}
      titleChanged = {changeTitleHandler}
      contentChanged = {changeContentHandler}
      submitText="Edit"
      submitClicked={updatedNoteHandler}
    />
    )}

    return (
      <div>
        {!editing && (
        <div className="note-operations">
          <a href="#" onClick={editNoteHandler}>
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#" onClick={deleteNoteHandler}>
            <i className="fa fa-trash" />
          </a>
        </div>)}
        {noteDisplay}
      </div>
    );
  };

  const addNoteHandler = () => {
    setCreating(true)
    setEditing(false)
    setTitle('')
    setContent('')
  }

  return (
    <div className="App">
      <NotesContainer>
         <NoteList>
           {notes.map(note => <Note key={note.id} title={note.title} 
           noteClicked={() => selectedNoteHandler(note.id)}
           active={selectedNote === note.id}/>)}
          </NoteList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
        </NotesContainer>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
       {validationErrors.length !== 0 && <Alert validationMessages={validationErrors}/>}
    </div>
  );
}

export default App;
